dp = [0] * 100000  # dp[i] = (頂点 i と頂点 1 との距離の期待値)
cum = [0] * 100001 # cum[i] = dp[0] + ... + dp[i - 1]
for i in range(1, 100000):
    dp[i] = (cum[i] * modinv(i) + 1) % MOD
    cum[i + 1] = (cum[i] + dp[i]) % MOD


t = -1  # number of test cases

def question1(n):
    return n * n


def question2(n):
    return (n ** 3 - n) * 1 + n * n


def question3(n):
    return t


def question4(n):
    return 4 * n ** 2 + 17


def question5(n):
    return pow(n, n ** 3, MOD)


def question6(n):
    return n


def question7(n):
    return cum[n] * modfact[n] % MOD


